function InitializeStressRelief(){
	$('#btnViewStressTipSheets').on('click', function(){
		LoadStressTipSheets();
		MovePageLeft('#pageStressTipSheet', _currentPage);
	})
	$('#btnViewStressReliefVideos').on('click', function(){
		MovePageLeft('#pageStressReliefVideos', _currentPage);
	})
	$('#pageStressReliefVideosClose').on('click', function(){
		MovePageLeft('#pageStressRelief', _currentPage);
	})
	$('#btnViewStressReliefAudio').on('click', function(){
		MovePageLeft('#pageStressReliefAudio', _currentPage);
	})
	$('#pageStressReliefAudioClose').on('click', function(){
		MovePageLeft('#pageStressRelief', _currentPage);
	})

	var list = $('.playVideoControl');
	for (var i = 0; i < list.length; i++) {
		var button = $(list[i]);
		var path = button.data('path');
		var vimeo =  path.substr(path.lastIndexOf('/') + 1);

		VideoClick(button, vimeo, path, 'stress');
	}

}

